mapper.insert:

    select seq_users.nextval from dual

insert into users (
    user_id, name, dept_id, dept_name, status, pos_id, rank_id, password, create_dt , manager_f
) values (
    #{user_id}, #{name}, #{dept_id}, #{dept_name}, #{status}, #{pos_id}, #{rank_id}, #{password}, #{create_dt}, #{manager_f}
)
